import Web3 from "web3";

export default class Web3Istance{
    constructor(){
        this.web3=()=>{
            if(typeof web3!==undefined)
                return new Web3(this.web3.currentProvider)
            else 
                return new Web3(new Web3.providers.HttpProvider('HTTP://127.0.0.1:8545'));
        }
    }

}
