export default  UsercheckProvider=({children})=>{
    const [account, setAccount] = useState('');

   async function checkAccount(){
        if (window.ethereum) {
            const accounts = await window.ethereum.request({
              method: "eth_accounts",
            });
            return accounts;

    }
}
    useEffect(()=>{
        setAccount("nwcncdcbjkbjkcs");  
    },[account])

    return (
        <UserContext.Provider
            value={{account,setAccount}}>
            {children}
        </UserContext.Provider>
    )
}
