//import { Outlet } from 'react-router-dom';
import './App.css';
//import { ethers } from "ethers";
import Homepage from './component/Homepaga/Homepage';
import {BrowserRouter,Navigate,Outlet,Route,Routes} from "react-router-dom";
import MyDesignes from './component/Mydesignes/mydesignes';
import DesignInfo from './component/myDesigneInfo/DesignInfo';
import MyPrinters from './component/Myprinters/MyPrinters';
import MetamaskLogin from './component/metamaskLogin/metamaskLogin';
import PrinterDetails from './component/Myprinters/PrinterDetails/PrinterDetails'
import SignIn from './component/signin/SignIn';
//import User from './SmartContracts/Users/Users';
import { useWeb3React } from '@web3-react/core'
import { InjectedConnector } from '@web3-react/injected-connector';
import { useState,useEffect } from 'react';

export const injected = new InjectedConnector({ supportedChainIds: [1, 3, 4, 5, 42, 56, 97,1337] });

 export function WalletConnected(){
  const { active: networkActive, error: networkError, activate: activateNetwork } = useWeb3React()
  const [loaded, setLoaded] = useState(false)

  useEffect(() => {
    injected
      .isAuthorized()
      .then((isAuthorized) => {
        setLoaded(true)
        if (isAuthorized && !networkActive && !networkError) {
          activateNetwork(injected)
        }
      })
      .catch(() => {
        setLoaded(true)
      })
  }, [activateNetwork, networkActive, networkError])

  if(loaded && networkActive)
    return <Outlet></Outlet>
  else if(loaded && !networkActive)
    return <Navigate to="login"></Navigate>
  else 
    return <span>Loading</span>


}





function App() {

  return(

    <BrowserRouter>
    <Routes>
      <Route path="/" element={<Homepage/>}></Route>
      <Route path="/login" element={<MetamaskLogin/>}></Route>
      <Route path="/signin" element={<SignIn/>}></Route>
      <Route exact path="/" element={<WalletConnected/>}>
        <Route path="myprinters"element={<MyPrinters/>}></Route>
          <Route path='myprinters/:printerDetails' element={<PrinterDetails/>}/>
        <Route path="mydesignes" element={<MyDesignes/>}></Route>
          <Route path="mydesignes/:designInfo" element={<DesignInfo/>}>
      </Route>

      </Route>



    </Routes>
    </BrowserRouter>
  )
}

export default App;
