import { useNavigate } from "react-router-dom";
import { HomeNav } from "../Navbar/homeNav";
import metamaskIcon from '../../images/metamsak.png'
import { useContext } from "react";
import { UserContext } from "../Context/userLogContext";
import './metamaskLogin.css'


export default function MetamaskLogin(){
    const navigate=useNavigate();
    const {account ,setAccount}=useContext(UserContext);

    const  enterPlatform=async ()=>{
        if(window.ethereum){
         // const  accounts=await window.ethereum.request({ method: "eth_requestAccounts"} );

          if(account>0){
           // localStorage.setItem("user", accounts[0])
            //console.log(account);
          }
          else {
            navigate('/',{replace:true});  
          }
        }
        else{alert("INSTALL METAMASK")}

   }
    return (
            <div>
              <HomeNav/>
              <div className="loginForm">

                <h2>Entra con Metamask</h2>
                <img className="metaIcon"src={metamaskIcon} alt=""></img>
                <div className="containerLogin">
                <button className="loginButton" onClick={enterPlatform}>Entra</button>
                </div>
              </div>
            </div>
    )
}
