import { useEffect, useState } from 'react';
import User from '../../SmartContracts/Users/Users' 
import { HomeNav } from '../Navbar/homeNav';
import './SignIn.css';

export default function SignIn(){

    const [error,setError]=useState(false)
    const [errorMessage,SetErrorMessage]=useState('');
    const [username,setUsername]=useState('');
    let user=new User();


    const handleSubmit= async(event)=>{
        event.preventDefault();
        navigator.geolocation.getCurrentPosition((position)=> {
            console.log("Latitude is :", position.coords.latitude);
            console.log("Longitude is :", position.coords.longitude);
            addCaller(position.coords.latitude,position.coords.longitude)
        });


    }


    const addCaller=async (latitude,longitude)=>{

        user.isUser(username,{latitude:latitude,longitude:longitude})
    }

    return (

        <div>
            <HomeNav/>
            <div className='SignIn'>
            <form  onSubmit={handleSubmit}>
                <label>
                    <p>Username</p>
                    <input type="text" name="username" value={username} onChange={event => setUsername(event.target.value)}autoComplete="off" required minLength="4" maxLength="10"></input>
                </label><br/>
                <button className="SubmitButton" type ="submit">Sign in</button>
            </form>
                { error && <h1 className='error'>{error}</h1>}
            </div>

        </div>


    )
}
