import { useWeb3React } from '@web3-react/core'
import { InjectedConnector } from '@web3-react/injected-connector';
import { useState,useEffect, Children } from 'react';
import {Navigate,Outlet, useNavigate} from "react-router-dom";



export const injected = new InjectedConnector({ supportedChainIds: [1, 3, 4, 5, 42, 56, 97 , 1337] });

 export default function WalletConnected({ children }){
  const { active: networkActive, error: networkError, activate: activateNetwork } = useWeb3React();
  const navigate=useNavigate();
  const [loaded, setLoaded] = useState(false);


  useEffect(() => {
    injected
      .isAuthorized()
      .then((isAuthorized) => {
        setLoaded(true)
        if (isAuthorized && !networkActive && !networkError) {
          activateNetwork(injected)
        }
      })
      .catch(() => {
        setLoaded(true)
      })
  }, [activateNetwork, networkActive, networkError])


  if(loaded){
    if(networkActive===true){
      console.log(loaded,networkActive);
      return children;
              }
    else if(networkActive===false){
      console.log(loaded,networkActive);
      return <Navigate to="/login" ></Navigate>
    }
  }

    else 
      return <span>Loading</span>


}
