import React, { useEffect,useState,createContext } from "react";
import Web3 from "web3";
import MetamaskLogin from "../metamaskLogin/metamaskLogin";

export const UserContext=createContext({});

const UserContextProvider=({children})=>{
   const [account, setAccount] = useState();
   const [loaded, setLoaded] = useState(true)

    async function checkAccount(){
        if (window.ethereum) {
            const accounts= await window.ethereum.request({ method: 'eth_accounts' });
            console.log(account)
            return accounts[0];


        }
    }
    async function result (){
        checkAccount();
        //console.log(account)

    } 

    useEffect(()=>{
        setAccount(checkAccount());

     },[])
    if(loaded)
        return (<UserContext.Provider value={{loaded}}> {children}</UserContext.Provider>)
    else return (<UserContext.Provider value={{loaded}}> <MetamaskLogin/></UserContext.Provider>)
}

export default UserContextProvider;
