import { useEffect, useState } from 'react';
import User from '../../SmartContracts/Users/Users' 
import { HomeNav } from '../Navbar/homeNav';
import { useNavigate } from 'react-router-dom';
import './SignIn.css';

export default function SignIn(){

    const [error,setError]=useState(false)
    const [errorMessage,SetErrorMessage]=useState('');
    const [username,setUsername]=useState('');
    let user=new User();
    let navigate=useNavigate();
    let prova='';


    useEffect(()=>{
        async function check (){

            if(window.ethereum){
                const accounts=await window.ethereum.request({ method: "eth_requestAccounts"} )
                /*if(accounts.length>0)
                    navigate('/')
                else
                    window.ethereum.request({ method: "eth_requestAccounts"} )*/
            }


        }
        check()
    },[])



    const handleSubmit= async(event)=>{
        console.log("c h")
        event.preventDefault();
        navigator.geolocation.getCurrentPosition((position)=> {
            console.log("Latitude is :", position.coords.latitude);
            console.log("Longitude is :", position.coords.longitude);
            //addCaller(position.coords.latitude,position.coords.longitude)
        });


    }

    const checkError=(latitude,longitude)=>{
        let isUser=user.isUser();
        if (isUser){
            SetErrorMessage("You are altready subscribed");
            setError(true);
            return true;
        }
        else if(latitude===undefined||longitude===undefined){
            SetErrorMessage("You must provide your location for signing in");
            setError(true); 
            return true;
        }
        else if(username.match( /^[a-zA-Z0-9!@]+$/g)){
            setError(true);
            SetErrorMessage("The inserted character are not valid");
            return true;
        }
        else return false;

    }
    const addCaller=async (latitude,longitude)=>{
        console.log("hchwjwe")
        /*let errors=checkError(latitude,longitude);
        console.log(errors)
        if (errors)
            return;
        else
            //user.addCaller(username,{latitude:latitude,longitude:longitude})
            console.log("errore");*/
    }       

    return (

        <div>
            <HomeNav/>
            <div className='SignIn'>
            <form  onSubmit={handleSubmit}>
                <label>
                    <p>Username</p>
                    <input type="text" name="username" value={username} onChange={event => setUsername(event.target.value)}autoComplete="off" required minLength="4" maxLength="10"></input>
                </label><br/>
                <button className="SubmitButton" type ="submit">Sign in</button>
            </form>
                {prova}
                { error && <h1 className='error'>{errorMessage}</h1>}
            </div>

        </div>


    )
}
