import { getDesigne } from "../../data/data";
import { useParams } from "react-router-dom";
import { UserNav } from "../Navbar/homeNav";
import { StlViewer } from "react-stl-file-viewer";
import cube from '../../images/stlFiles/Cube_3d.stl'
import './DesignInfo.css'

export default function DesignInfo(){
    let param = useParams();
    let design=getDesigne(param.designInfo)
    const [volume, setvolume] = useState(0);

    return(
        <div>
            <UserNav/>
            <div className="stl_render">
                <h1>{design.name}</h1>
                <StlViewer className="viewerPosition"
                       width={500}
                       height={500}
                       url={design.stl_path}
                       groundColor="rgb(255, 255, 255)"
                       objectColor="rgb(50, 40, 255)"
                       skyboxColor="rgb(255, 255, 255)"
                       gridLineColor="rgb(0, 0, 0)"
                       lightColor="rgb(255, 255, 255)"
                       volume={setvolume}/>
                <div className="designInfo">                    
                    <p>{design.info}</p>
                    <button className="printButton">Stampa</button>
                </div>
            </div>
        </div>
    )


}
