   const [account, setAccount] = useState();
   const [loaded, setLoaded] = useState(true)


    async function checkAccount(){
        if (window.ethereum) {
            const accounts= await window.ethereum.request({ method: 'eth_accounts' });
            console.log(account)
            return accounts[0];


        }
    }
    async function result (){
        checkAccount();
        //console.log(account)

